graphql.query:
mutation {
  createStudent(
    createStudentRequest: {
      firstName: "Prakash"
      lastName: "Shelke"
      email: "asf@gmail.com"
      street: "14"
      city: "Bhilai"
      subjectsLearning: [
        { subjectName: "Java 8", marksObtained: 100 }
        { subjectName: "Springboot", marksObtained: 100 }
      ]
    }
  ) {
    id
    firstName
    lastName
    email
    street
    city
    learningSubjects {
      id
      subjectName
      marksObtained
    }
  }
}

mutation createStudent($createStudentRequest : CreateStudentRequest){
  createStudent(createStudentRequest : $createStudentRequest) {
    id
    firstName
    lastName
    email
    street
    city
    learningSubjects {
      id
      subjectName
      marksObtained
    }
  }
}

query getStudent($id : Int, $subs : [String]) {
  getStudent(id : $id) {
    id
    firstName
    lastName
    email
    city
    learningSubjects(subjects : $subs) {
      id
      subjectName
      marksObtained
    }
  }
}

